import React,{useState} from 'react';
import './Signin.css';
import imgUsername from'./images/user.png';
import imgPassword from'./images/password.png';


import {Link} from 'react-router-dom';
import Axios from 'axios';

import {BrowserRouter as Router,Switch,Route} from 'react-router-dom';









function Signin() {

const [username,setUsername] =useState('');
const [password,setPassword] =useState('');


const[loginstatus,setLoginstatus,]=useState('')



const signin=()=>{

Axios.post("http://localhost:3030/signin",{
    username:username,
    password:password,
}).then((response) =>{

    if(response.data.message){
        setLoginstatus(response.data.message);
      }

      else{

        setLoginstatus(response.data[0].username);

        <Router>
            <Switch>
            <Route path="/signup" component={Signup} />

            </Switch>

        </Router>



      }

});
       
};


    return (
        <div className="signin">
            <div className="signinLeft">
                <div className="textbox"><img src={imgUsername} alt="as" /><input type="text" placeholder="User Name" onChange={(e)=> {setUsername(e.target.value)}} /></div> 
                <div className="textbox"><img src={imgPassword } alt="AS" /><input onChange={(e)=> {setPassword(e.target.value)}} type="password" placeholder="Password" /></div>

                <div className="checkboxandbutton">
                <label htmlFor="lbl01">Remember Me</label>
                <input type="checkbox" name="lbl01" />
                <button onClick={signin}>LOGIN</button>
                </div>

               

                <div className="register">


        


                    <Link to="/Home1">Register Now</Link>
                          
                    
                    
                    <p>Forgot Password</p>

                </div>

               <div className="socialmedia">

               <div className="loginFacebook">
                    <button>LOGIN WITH FACEBOOK</button>
                </div>

                <div className="loginGoogle">
                    <button>LOGIN WITH GOOGLE</button>
                </div>
                
               </div>

            </div>

            <div className="signinRight">
                <h1>Welcome Back...!</h1>

                <p>To keep connected with us please  <br/>
                    login with your personal info </p>
            </div>
        </div>
    )
}

export default Signin
