import React,{useState} from 'react'
import styled from 'styled-components';





const button = styled.button`
height:25px;
width:100px;
background-color:${({open}) => open ? 'red' : 'grey'};
top:15px;
position:relative;
border-radius:5px;
    
    
 
 
    
    
}
`;





function Button() {

    const [open,setOpen]=useState(false);

  

    return (
        <div>
         
            <button className="btnlogin" open={open} onClick={() => setOpen(!open)}></button>
           

        </div>
    )
}

export default Button
