import React,{useState} from 'react'
import Axios from 'axios';

import './Signup.css'
import imgusername from './images/icon/user.png'
import imgemail from './images/icon/email.png'
import imgcontacts from './images/icon/contacts.png'
import imgprofession from './images/icon/profession.png'
import imgpassword from './images/icon/password.png'
import imgfacebook from './images/icon/facebook.png'
import imggoogle from './images/icon/google.png'

import Login from './google/Googlesigin';
import Navbar from '../navbar/Navbar';
import Footer from '../footer/Footer';
import {useHistory} from 'react-router-dom';


import {userSchema} from './UserValidation.js'
import * as Yup from "yup";
import { Formik, Form,Field } from 'formik';
import { TextField } from './TextField';




const Signup = () => {
  const validate = Yup.object({
    userName: Yup.string()
      .max(15, 'Must be 15 characters or less')
      .required('Required'),
    lastName: Yup.string()
      .max(20, 'Must be 20 characters or less')
      .required('Required'),
    email: Yup.string()
      .email('Email is invalid')
      .required('Email is required'),
    password: Yup.string()
      .min(6, 'Password must be at least 6 charaters')
      .required('Password is required'),
    confirmPassword: Yup.string()
      .oneOf([Yup.ref('password'), null], 'Password must match')
      .required('Confirm password is required'),
  })


  const [username,setUsername]=useState('');
  const[Msgusername,setMsgusername]=useState('');
  return (
    <Formik
      initialValues={{
        userName: '',
        lastName: '',
        email: '',
        password: '',
        confirmPassword: ''
      }}
      validationSchema={validate}
      onSubmit={values => {
        console.log(values)
      }}
    >
     {({errors,touched})=>(


        <Form>

            <label>UserName</label>
            <div><Field name="userName" /></div>
            <div className="input-text"><img src={imgusername}/><Field name="userName" onChange={(e)=> {setUsername(e.target.value);{setMsgusername("Enter your Name")};}} type="text" name="" placeholder="Username"/></div>
              {errors.userName && touched.userName ? (
                    <div style={{color:'red'}}>
                    {errors.userName}

                    </div>

                ):null}



        </Form>

        )}



    </Formik>
  )
}

export default Signup;
