import React,{useState} from 'react'
import styled from 'styled-components';
import { Link } from 'react-router-dom';




const Ul = styled.ul`
    list-style:none;
    display:flex;
    flex-flow:row nowrap;
    right:100px;
    position:relative;
    font-size:14px;
    font-weight:bold;
    

li{
    padding:18px;
    color:black;
    text-decoration-style:none;
    
}
.btnlogin{
    height:25px;
    width:100px;
    background-color:#d3aa05;
    top:15px;
    position:relative;
    border-radius:5px;
}


@media(max-width:768px){
    
    flex-flow:column nowrap;
    background-color:grey;
    position:fixed;
    transform:${({open}) => open ? 'translateX(0)' : 'translateX(100%)'};
    top:100px;
    right:0;
    height:500px;
    width:400px;
    padding-top:3.5rem;
    transition:transform 0.3s ease-in-out;

    li{

        color:violet;
    }

    
    
}
`;





function RightNav({open}) {

    const [btnstate,setBtnstate]=useState(false);

  

    return (
        <div>
             <Ul open={open}>

            <Link to='/'>
            <li>HOME</li>
            </Link>
            
            <Link to="/signup"><li>BUY A COURSE</li></Link>
            <Link to="/explorecourse"><li>EXPLORE COURSE</li></Link>
            <Link to="/signup"><li>CREATE ACCOUNT</li></Link>{({btntata})=>btntata ? "login" :"logout"}
            <Link to="/signin"><button className="btnlogin" btntata={btnstate} onclick={() => setBtnstate(!btnstate)}></button></Link>
            </Ul>

        </div>
    )
}

export default RightNav
