import React,{useState,useContext} from 'react'
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import Button from '../button/Buttonlogin';
import {Usercontext} from '../../App';






const Ul = styled.ul`
    list-style:none;
    display:flex;
    flex-flow:row nowrap;
    right:100px;
    position:relative;
    font-size:14px;
    font-weight:bold;
    text-decoration: none;
    
    
 
    

li{
    padding:18px;
    color:black;
    text-decoration-style:none;
    
}


li:hover{
    color:#FFCC01;
}



@media(max-width:768px){
    
    flex-flow:column nowrap;
    background-color:grey;
    position:fixed;
    transform:${({open}) => open ? 'translateX(0)' : 'translateX(100%)'};
    top:100px;
    right:0;
    height:500px;
    width:400px;
    padding-top:3.5rem;
    transition:transform 0.3s ease-in-out;

    li{

        color:violet;
    }

    
    
}
`;





function RightNav({open}) {

    const [btnstate,setBtnstate]=useState(false);

    const {state,dispatch} =useContext(Usercontext);

    const RenderMenu =()=>{
            if(state){

                return(


            <Ul>

            <Link to='/'><li>HOME</li></Link>
            <Link to="/signup"><li>BUY A COURSE</li></Link>
            <Link to="/explorecourse"><li>EXPLORE COURSE</li></Link>
            <Link to="/signin"><li>LOGOUT</li></Link>

            </Ul>

                )
            }

            else{

                return(

                    <div>

                    <Link to='/'><li>HOME</li></Link>
                    <Link to="/signup"><li>BUY A COURSE</li></Link>
                    <Link to="/explorecourse"><li>EXPLORE COURSE</li></Link>
                    <Link to="/signup"><li>CREATE ACCOUNT</li></Link>
                    <Link to="/signin"><li>LOGIN</li></Link>
        
                    </div>
                    
                )
            }
    }

  

    return (
        <div>
             <Ul open={open}>

         

            <RenderMenu />

            {/* <Link to="/signin"><Button /></Link> */}
            </Ul>

        </div>
    )
}

export default RightNav
